    private void ButtonGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonGuardarActionPerformed
        // TODO add your handling code here:
        int calorias,cantidad;
        String Nombre;
        calorias = (parseInt(TextFieldCalorias.getText()));
        cantidad = (parseInt(TextFieldCantidad.getText()));
        Nombre = (TextFieldNombre.getText());
        ProductoSano producto = new ProductoSano(Nombre,cantidad,calorias);
        if(ComboBoxVegano.getSelectedIndex()==0){
           producto.setVegan(true); 
        }
        if(ComboBoxVegano.getSelectedIndex()==1){
            producto.setVegan(false); 
        }
        Principal.ListaFecha.get(Fechas.variable).getListaProducto().add(producto);
        Productos ventana = new Productos();
        ventana.setVisible(true);
        
         this.setVisible(false);
    }//GEN-LAST:event_ButtonGuardarActionPerformed

